#!/usr/bin/env tsx

/**
 * Kanban MCP Integrated Server Test Suite
 * 
 * Tests the integrated MCP server endpoints that are part of the main Kanban application.
 * This version runs on the same port as the main app (5000) under the /mcp path.
 */

const SERVER_URL = process.env.SERVER_URL || 'http://localhost:5000';

interface McpRequest {
  jsonrpc: string;
  id: number;
  method: string;
  params?: any;
}

interface McpResponse {
  jsonrpc: string;
  id: number;
  result?: any;
  error?: any;
}

// Helper function to make MCP requests
async function mcpRequest(method: string, params?: any): Promise<any> {
  const request: McpRequest = {
    jsonrpc: "2.0",
    id: Date.now(),
    method,
    params
  };

  const response = await fetch(`${SERVER_URL}/mcp`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(request)
  });

  if (!response.ok) {
    throw new Error(`HTTP ${response.status}: ${response.statusText}`);
  }

  const data: McpResponse = await response.json();

  if (data.error) {
    throw new Error(`MCP Error: ${data.error.message}`);
  }

  return data.result;
}

// Test functions
async function testHealthCheck() {
  console.log('üè• Testing MCP health check...');
  const response = await fetch(`${SERVER_URL}/mcp/health`);
  const health = await response.json();
  console.log('‚úÖ Health check:', health.status, '- Server:', health.mcpServer);
  return health;
}

async function testServerInfo() {
  console.log('üìã Testing MCP server info...');
  const response = await fetch(`${SERVER_URL}/mcp/info`);
  const info = await response.json();
  console.log(`‚úÖ Server info: ${info.name} v${info.version} with ${info.tools.length} tools`);
  return info;
}

async function testListTools() {
  console.log('üîß Testing list tools...');
  const result = await mcpRequest('tools/list');
  console.log(`‚úÖ Found ${result.tools.length} tools:`);
  result.tools.forEach((tool: any) => {
    console.log(`   ‚Ä¢ ${tool.name}: ${tool.description}`);
  });
  return result.tools;
}

async function testGetCards() {
  console.log('üìã Testing get cards...');
  const result = await mcpRequest('tools/call', {
    name: 'get_cards',
    arguments: {}
  });
  const cards = JSON.parse(result.content[0].text);
  console.log(`‚úÖ Retrieved ${cards.length} cards`);
  return cards;
}

async function testGetCardsByStatus() {
  console.log('üìä Testing get cards by status...');
  const result = await mcpRequest('tools/call', {
    name: 'get_cards_by_status',
    arguments: {}
  });
  const grouped = JSON.parse(result.content[0].text);
  const statusCounts = Object.keys(grouped).map(status => 
    `${status}: ${grouped[status].length}`
  ).join(', ');
  console.log(`‚úÖ Cards by status: ${statusCounts}`);
  return grouped;
}

async function testCreateCard() {
  console.log('‚ûï Testing create card...');
  const result = await mcpRequest('tools/call', {
    name: 'create_card',
    arguments: {
      title: `Test Card ${Date.now()}`,
      description: 'This is a test card created via integrated MCP',
      status: 'not-started'
    }
  });
  console.log('‚úÖ Create card result:', result.content[0].text.split('\n')[0]);

  // Extract card ID from response
  const cardData = JSON.parse(result.content[0].text.split(':\n')[1]);
  return cardData.id;
}

async function testMoveCard(cardId: string) {
  console.log('üîÑ Testing move card...');
  const result = await mcpRequest('tools/call', {
    name: 'move_card',
    arguments: {
      id: cardId,
      status: 'in-progress',
      position: 0
    }
  });
