  return result;
}

async function testUpdateCard(cardId: string) {
  console.log('‚úèÔ∏è Testing update card...');
  const result = await mcpRequest('tools/call', {
    name: 'update_card',
    arguments: {
      id: cardId,
      title: 'Updated Test Card',
      description: 'This card has been updated via MCP'
    }
  });
  console.log('‚úÖ Update card result:', result.content[0].text.split('\n')[0]);
  return result;
}

async function testGetCard(cardId: string) {
  console.log('üîç Testing get specific card...');
  const result = await mcpRequest('tools/call', {
    name: 'get_card',
    arguments: {
      id: cardId
    }
  });
  const card = JSON.parse(result.content[0].text);
  console.log(`‚úÖ Retrieved card: "${card.title}" - ${card.status}`);
  return card;
}

async function testBatchMove(cardId: string) {
  console.log('üì¶ Testing batch move cards...');
  const result = await mcpRequest('tools/call', {
    name: 'batch_move_cards',
    arguments: {
      operations: [
        { cardId, status: 'complete', position: 0 }
      ]
    }
  });
  console.log('‚úÖ Batch move result:', result.content[0].text.split('\n')[0]);
  return result;
}

async function testDeleteCard(cardId: string) {
  console.log('üóëÔ∏è Testing delete card...');
  const result = await mcpRequest('tools/call', {
    name: 'delete_card',
    arguments: {
      id: cardId
    }
  });
  console.log('‚úÖ Delete card result:', result.content[0].text);
  return result;
}

// Main test runner
async function runTests() {
  console.log(`üöÄ Testing Kanban MCP Integrated Server at ${SERVER_URL}\n`);

  try {
    // Test health and info endpoints
    await testHealthCheck();
    await testServerInfo();
    console.log();

    // Test MCP protocol endpoints
    await testListTools();
    console.log();

    // Test card operations
    await testGetCards();
    await testGetCardsByStatus();
    console.log();

    // Test card lifecycle
    const cardId = await testCreateCard();
    await testMoveCard(cardId);
    await testUpdateCard(cardId);
    await testGetCard(cardId);
    await testBatchMove(cardId);
    await testDeleteCard(cardId);
    console.log();

    console.log('üéâ All tests passed! Integrated MCP Server is working correctly.');
    console.log('üîó The MCP server is fully integrated with the Kanban application.');
    console.log('üì° AI agents can now access the MCP endpoints at the same URL as the web app.');

  } catch (error) {
    console.error('‚ùå Test failed:', error);
    process.exit(1);
  }
}

// Run tests if this script is executed directly
if (import.meta.url === `file://${process.argv[1]}`) {
  runTests();
}

