import { useDroppable } from "@dnd-kit/core";
import { SortableContext, verticalListSortingStrategy } from "@dnd-kit/sortable";
import { motion, AnimatePresence } from "framer-motion";
import { TaskCard } from "./task-card";
import { Card, KanbanStatus } from "@shared/schema";
import { cn } from "@/lib/utils";
import { GripVertical } from "lucide-react";
import { useState, useRef } from "react";

interface KanbanColumnProps {
  id: KanbanStatus;
  title: string;
  color: string;
  bgColor: string;
  cards: Card[];
  count: number;
  width?: number;
  onWidthChange?: (width: number) => void;
  onEditCard?: (card: Card) => void;
}

export function KanbanColumn({ id, title, color, bgColor, cards, count, width = 320, onWidthChange, onEditCard }: KanbanColumnProps) {
  const { setNodeRef, isOver } = useDroppable({
    id: id,
  });

  const [isResizing, setIsResizing] = useState(false);
  const resizeRef = useRef<HTMLDivElement>(null);
  const startXRef = useRef(0);
  const startWidthRef = useRef(0);

  const getStatusDotColor = () => {
    const colors = {
      gray: "bg-gradient-to-r from-gray-400 to-gray-500 shadow-md",
      red: "bg-gradient-to-r from-red-400 to-red-500 shadow-md", 
      blue: "bg-gradient-to-r from-blue-400 to-blue-500 shadow-md",
      green: "bg-gradient-to-r from-green-400 to-green-500 shadow-md",
      purple: "bg-gradient-to-r from-purple-400 to-purple-500 shadow-md",
    };
    return colors[color as keyof typeof colors] || "bg-gradient-to-r from-gray-400 to-gray-500 shadow-md";
  };

  const getCountBadgeColor = () => {
    const colors = {
      gray: "bg-gradient-to-r from-gray-100 to-gray-200 text-gray-700 shadow-sm",
      red: "bg-gradient-to-r from-red-100 to-red-200 text-red-700 shadow-sm",
      blue: "bg-gradient-to-r from-blue-100 to-blue-200 text-blue-700 shadow-sm", 
      green: "bg-gradient-to-r from-green-100 to-green-200 text-green-700 shadow-sm",
      purple: "bg-gradient-to-r from-purple-100 to-purple-200 text-purple-700 shadow-sm",
    };
    return colors[color as keyof typeof colors] || "bg-gradient-to-r from-gray-100 to-gray-200 text-gray-700 shadow-sm";
  };

  const getHeaderGradient = () => {
    const gradients = {
      gray: "bg-gradient-to-r from-gray-50 to-gray-100 dark:from-gray-800 dark:to-gray-700",
      red: "bg-gradient-to-r from-red-50 to-red-100 dark:from-red-900/30 dark:to-red-800/30",
      blue: "bg-gradient-to-r from-blue-50 to-blue-100 dark:from-blue-900/30 dark:to-blue-800/30",
      green: "bg-gradient-to-r from-green-50 to-green-100 dark:from-green-900/30 dark:to-green-800/30",
      purple: "bg-gradient-to-r from-purple-50 to-purple-100 dark:from-purple-900/30 dark:to-purple-800/30",
    };
    return gradients[color as keyof typeof gradients] || "bg-gradient-to-r from-gray-50 to-gray-100 dark:from-gray-800 dark:to-gray-700";
  };

  const handleMouseDown = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setIsResizing(true);
    startXRef.current = e.clientX;
    startWidthRef.current = width;

    const handleMouseMove = (e: MouseEvent) => {
      const deltaX = e.clientX - startXRef.current;
      const newWidth = Math.max(280, Math.min(600, startWidthRef.current + deltaX));

      if (onWidthChange) {
        onWidthChange(newWidth);
      }
    };

    const handleMouseUp = () => {
      setIsResizing(false);
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };

    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
  };

  return (
    <div 
      ref={setNodeRef}
      className={cn(
        "kanban-column rounded-xl overflow-hidden flex-shrink-0 relative group min-h-full transition-all duration-300 ease-in-out",
        isOver && "ring-2 ring-blue-400 ring-opacity-60 shadow-xl scale-[1.02]"
      )}
      style={{ width: `${width}px` }}
    >
      <div className="min-h-full">
        <div className={cn("px-5 py-4 border-b border-white/20 dark:border-gray-600/20", getHeaderGradient())}>
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className={cn("w-4 h-4 rounded-full", getStatusDotColor())}></div>
              <h3 className="font-bold text-gray-800 dark:text-gray-200 text-sm tracking-wide uppercase">{title}</h3>
            </div>
            <span className={cn("text-xs px-3 py-1.5 rounded-full font-bold", getCountBadgeColor())}>
              {count}
            </span>
          </div>
        </div>

        <div
          className={cn(
            "p-5 min-h-[500px] transition-all duration-300 bg-white/50 dark:bg-gray-800/50",
            isOver && "bg-gradient-to-b from-blue-50/50 to-blue-100/30 dark:from-blue-900/30 dark:to-blue-800/20"
          )}
        >
          <SortableContext items={cards.map(card => card.id)} strategy={verticalListSortingStrategy}>
            <motion.div className="space-y-3" layout>
              <AnimatePresence mode="popLayout">
                {cards.map(card => (
                  <motion.div
                    key={card.id}
                    layoutId={`card-${card.id}`}
                    initial={{ opacity: 0, scale: 0.8 }}
                    animate={{ opacity: 1, scale: 1 }}
                    exit={{ opacity: 0, scale: 0.8 }}
                    transition={{ 
                      layout: { duration: 0.5, ease: "easeInOut" },
                      opacity: { duration: 0.3 },
                      scale: { duration: 0.3 }
                    }}
                  >
                    <TaskCard card={card} onEdit={onEditCard} />
                  </motion.div>
                ))}
              </AnimatePresence>
            </motion.div>
          </SortableContext>

          {/* Drop zone for empty columns - make it more prominent when empty */}
          {cards.length === 0 ? (
            <div className={cn(
              "h-40 w-full border-2 border-dashed border-gray-300 dark:border-gray-600 rounded-lg flex items-center justify-center transition-all duration-200",
              isOver && "border-blue-400 bg-blue-50/50 dark:bg-blue-900/20 border-solid"
            )}>
              <p className="text-gray-500 dark:text-gray-400 text-sm font-medium">
                Drop cards here
              </p>
            </div>
          ) : (
            <div className="h-16 w-full" />
          )}
        </div>
      </div>

      {/* Resize handle */}
      <div
        ref={resizeRef}
        onMouseDown={handleMouseDown}
        className={cn(
          "absolute top-0 right-0 w-3 h-full cursor-col-resize opacity-0 group-hover:opacity-100 transition-opacity duration-200 flex items-center justify-center z-10 bg-transparent hover:bg-blue-50/20",
          isResizing && "opacity-100 bg-blue-50/30"
        )}
        title="Drag to resize column"
      >
        <div className="w-1 h-12 bg-gray-400 dark:bg-gray-500 rounded-full hover:bg-blue-500 dark:hover:bg-blue-400 transition-colors shadow-sm"></div>
      </div>
    </div>
  );
}

client/src/components/task-card.tsx.content:
import { useSortable } from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";
import { motion, AnimatePresence } from "framer-motion";
import { GripVertical, ExternalLink, CheckCircle, AlertTriangle, Shield, Trash2, ChevronDown, ChevronUp, Edit3, Copy, Check, Eye } from "lucide-react";
import { Card, KanbanStatus } from "@shared/schema";
import { cn } from "@/lib/utils";
import { ExplosionAnimation } from "./explosion-animation";
import { parseTaskList, calculateTaskProgress, updateTaskCompletion, hasTaskList, serializeTaskList, extractTasksFromMarkdown, TaskItem } from "@/lib/task-utils";
import { InteractiveMarkdown } from "./interactive-markdown";
import { ViewCardDialog } from "./view-card-dialog";
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";

interface TaskCardProps {
  card: Card & { 
    _remoteUpdate?: boolean; 
    _statusChanged?: boolean; 
  };
  onEdit?: (card: Card) => void;
}

export function TaskCard({ card, onEdit }: TaskCardProps) {
  const [isExploding, setIsExploding] = useState(false);
  const [shouldHide, setShouldHide] = useState(false);
  const [isExpanded, setIsExpanded] = useState(card.status === "in-progress");
  const [justCopied, setJustCopied] = useState(false);
  const [showViewDialog, setShowViewDialog] = useState(false);
  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Extract tasks from markdown and sync with stored task list
  const markdownTasks = extractTasksFromMarkdown(card.description);
  const storedTasks = parseTaskList(card.taskList);

  // Merge markdown tasks with stored completion status, preferring stored state
  const tasks = markdownTasks.map(markdownTask => {
    const storedTask = storedTasks.find(stored => stored.text === markdownTask.text);
    return storedTask || markdownTask;
  });

  const taskProgress = calculateTaskProgress(tasks);
  const showProgress = tasks.length > 0;

  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({ id: card.id });

  const deleteMutation = useMutation({
    mutationFn: () => apiRequest("DELETE", `/api/cards/${card.id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cards"] });
      toast({
        title: "Card deleted",
        description: "The card has been successfully removed.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete card. Please try again.",
        variant: "destructive",
      });
      // Reset animation state on error
      setIsExploding(false);
      setShouldHide(false);
    },
  });

  const updateTaskMutation = useMutation({
    mutationFn: async (updatedTasks: TaskItem[]) => {
      const response = await apiRequest("PATCH", `/api/cards/${card.id}`, {
        taskList: serializeTaskList(updatedTasks)
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cards"] });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update task progress.",
        variant: "destructive",
      });
    },
  });

  const handleDelete = (e: React.MouseEvent) => {
    e.stopPropagation();
    setIsExploding(true);
  };

  const handleExplosionComplete = () => {
    setShouldHide(true);
    deleteMutation.mutate();
  };

  const handleTaskToggle = (taskText: string, completed: boolean) => {
    const updatedTasks = tasks.map(task => 
      task.text === taskText ? { ...task, completed } : task
    );
    updateTaskMutation.mutate(updatedTasks);
  };

  const handleEdit = (e: React.MouseEvent) => {
    e.stopPropagation();
    if (onEdit) {
      onEdit(card);
    }
  };

  const handleCopy = async (e: React.MouseEvent) => {
    e.stopPropagation();
    try {
      const cardText = `# ${card.title}\n\n${card.description}${card.link ? `\n\nLink: ${card.link}` : ''}`;
      await navigator.clipboard.writeText(cardText);
      setJustCopied(true);
      toast({
        title: "Copied to clipboard",
        description: "Card content has been copied as Markdown.",
      });
      setTimeout(() => setJustCopied(false), 2000);
    } catch (error) {
      toast({
        title: "Copy failed",
        description: "Unable to copy to clipboard. Please try again.",
        variant: "destructive",
      });
    }
  };

  // Hide the card completely after explosion
  if (shouldHide) {
    return null;
  }

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
  };

  const getBorderColor = () => {
    const colors: Record<KanbanStatus, string> = {
      "not-started": "border-l-4 border-l-gray-400",
      "blocked": "border-l-4 border-l-red-500", 
      "in-progress": "border-l-4 border-l-blue-500",
      "complete": "border-l-4 border-l-green-500",
      "verified": "border-l-4 border-l-purple-500",
    };
    return colors[card.status as KanbanStatus] || "border-l-4 border-l-gray-400";
  };

  const getStatusIcon = () => {
    switch (card.status) {
      case "blocked":
        return <AlertTriangle className="w-3 h-3 text-red-500" />;
      case "in-progress":
        return <div className="w-2 h-2 rounded-full bg-blue-500 animate-pulse" />;
      case "complete":
        return <CheckCircle className="w-3 h-3 text-green-500" />;
      case "verified":
        return <Shield className="w-3 h-3 text-purple-500" />;
      default:
        return null;
    }
  };

  return (
    <ExplosionAnimation 
      trigger={isExploding} 
      onComplete={handleExplosionComplete}
    >
      <div
        ref={setNodeRef}
        style={style}
        {...attributes}

        className={cn(
          "task-card rounded-xl p-5 group relative flex flex-col min-w-[280px] bg-white/90 dark:bg-gray-800/90 backdrop-blur-sm transition-colors duration-300",
          getBorderColor(),
          isDragging && "opacity-60 scale-105 rotate-2 shadow-2xl z-50",
          card._remoteUpdate && card._statusChanged && "ring-2 ring-blue-400 ring-opacity-75 animate-pulse",
          isExpanded && "min-h-fit"
        )}
      >
        <div className="flex items-start justify-between mb-2">
          <div className="flex-1 pr-2">
            <h4 className="font-medium text-gray-900 dark:text-gray-100 break-words mb-1">{card.title}</h4>
            {/* Project tag */}
            {card.project && (
              <div className="flex items-center">
                <span className="inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800 dark:bg-blue-900/30 dark:text-blue-300">
                  {card.project}
                </span>
              </div>
            )}
          </div>

          {/* Action buttons container */}
          <div className="flex items-center space-x-1 ml-2 flex-shrink-0">
            {/* Copy button - only show on hover */}
            <button
              onClick={handleCopy}
              className="opacity-0 group-hover:opacity-100 transition-opacity duration-200 p-1 rounded-md hover:bg-blue-100 dark:hover:bg-blue-900/30 text-blue-500 dark:text-blue-400 hover:text-blue-700 dark:hover:text-blue-300 z-10"
              title="Copy card content"
            >
              {justCopied ? (
                <Check className="w-3 h-3 text-green-500" />
              ) : (
                <Copy className="w-3 h-3" />
              )}
            </button>

            {/* View button - only show on hover */}
            <button
              onClick={() => setShowViewDialog(true)}
              className="opacity-0 group-hover:opacity-100 transition-opacity duration-200 p-1 rounded-md hover:bg-purple-100 dark:hover:bg-purple-900/30 text-purple-600 dark:text-purple-400 hover:text-purple-700 dark:hover:text-purple-300 z-10"
              title="View card details"
            >
              <Eye className="w-3 h-3" />
            </button>

            {/* Edit button - only show on hover */}
            <button
              onClick={handleEdit}
              className="opacity-0 group-hover:opacity-100 transition-opacity duration-200 p-1 rounded-md hover:bg-green-100 dark:hover:bg-green-900/30 text-green-600 dark:text-green-400 hover:text-green-700 dark:hover:text-green-300 z-10"
              title="Edit card"
            >
              <Edit3 className="w-3 h-3" />
            </button>

            {/* Delete button - only show on hover */}
            <button
              onClick={handleDelete}
              className="opacity-0 group-hover:opacity-100 transition-opacity duration-200 p-1 rounded-md hover:bg-red-100 dark:hover:bg-red-900/30 text-red-500 dark:text-red-400 hover:text-red-700 dark:hover:text-red-300 z-10"
              title="Delete card"
            >
              <Trash2 className="w-3 h-3" />
            </button>

            {/* Drag handle */}
            <div className="p-1 cursor-grab active:cursor-grabbing" {...listeners}>
              <GripVertical className="w-4 h-4 text-gray-400 dark:text-gray-500 hover:text-gray-600 dark:hover:text-gray-300 transition-colors" />
            </div>
          </div>
        </div>

        {/* Task Progress Bar */}
        {showProgress && (
          <div className="mb-3">
            <div className="flex items-center justify-between mb-2">
              <span className="text-xs text-gray-600 dark:text-gray-300 font-medium">
                Progress: {taskProgress.completed}/{taskProgress.total} tasks
              </span>
              <span className="text-xs text-gray-500 dark:text-gray-400 font-bold">
                {taskProgress.percentage}%
              </span>
            </div>
            <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2">
              <motion.div
                className={cn(
                  "h-2 rounded-full transition-all duration-500",
                  taskProgress.percentage === 100 ? "bg-green-500" : "bg-blue-500"
                )}
                initial={{ width: 0 }}
                animate={{ width: `${taskProgress.percentage}%` }}
                transition={{ duration: 0.5, ease: "easeOut" }}
              />
            </div>
          </div>
        )}

        <div className="mb-3 flex-1">
          <motion.div
            initial={false}
            animate={{ 
              height: isExpanded ? "auto" : "auto"
            }}
            transition={{ duration: 0.2, ease: "easeInOut" }}
          >
            <InteractiveMarkdown
              content={card.description}
              tasks={tasks}
              onTaskToggle={handleTaskToggle}
              isExpanded={isExpanded}
            />
          </motion.div>

          {/* Show expand/collapse button only if content is long enough */}
          {card.description && card.description.length > 100 && (
            <motion.button
              onClick={(e) => {
                e.stopPropagation();
                setIsExpanded(!isExpanded);
              }}
              className={cn(
                "mt-2 text-xs flex items-center space-x-1 font-medium transition-colors duration-150",
                card.status === "in-progress" 
                  ? "text-blue-700 hover:text-blue-900" 
                  : "text-blue-600 hover:text-blue-800"
              )}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <span>{isExpanded ? "Show less" : "Show more"}</span>
              <motion.div
                animate={{ rotate: isExpanded ? 180 : 0 }}
                transition={{ duration: 0.2 }}
              >
                <ChevronDown className="w-3 h-3" />
              </motion.div>
            </motion.button>
          )}


        </div>

        <div className="flex items-center justify-between mt-auto">
          {card.link ? (
            <a
              href={card.link}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-600 hover:text-blue-700 text-sm font-medium flex items-center space-x-1"
              onClick={(e) => e.stopPropagation()}
            >
              <span>View Details</span>
              <ExternalLink className="w-3 h-3" />
            </a>
          ) : (
            <div></div>
          )}

          <div className="flex items-center space-x-2">
            {getStatusIcon()}
            <span className="text-xs text-gray-500">ID: {card.id.slice(0, 8)}</span>
          </div>
        </div>
      </div>

      {/* View Dialog */}
      <ViewCardDialog
        card={card}
        open={showViewDialog}
        onOpenChange={setShowViewDialog}
        onEditCard={onEdit}
      />
    </ExplosionAnimation>
  );
}

